import React, { useEffect, useState } from 'react';
import api from './api';

/* Bootstrap */
import { Button, Modal, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function App() {

  /* States of components  */
  const [tools, setTools] = useState([]);
  const [toolName, setToolName] = useState('');
  const [toolLink, setToolLink] = useState('');
  const [toolDescription, setToolDescription] = useState('');
  const [toolTags, setToolTags] = useState(["customize", "reactJS", "notchange"]);
  const [selectedTool, setSelectedTool] = useState();

  /* States of Modals */
  const [showModalAdd, setShowModalAdd] = useState(false);
  const [showModalRemove, setShowModalRemove] = useState(false);

  /* States of Inputs */
  const [inputSearch, setInputSearch] = useState('');
  const [checkbox, setCheckbox] = useState(false);

  /* Modal Controllers - Add Tools */
  const handleCloseModalAdd = () => {
    setShowModalAdd(false);
    window.location.reload(false);
  };

  const handleShowModalAdd = () => {
    setShowModalAdd(true);
  };

  /* Modal Controllers - Remove Tools */
  const handleCloseModalRemove = () => {
    setShowModalRemove(false);
    window.location.reload(false);
  };

  const handleShowModalRemove = () => {
    setShowModalRemove(true);
  };

  /* Initialization API with all items */
  useEffect(() => {
    api
      .get("")
      .then((response) => setTools(response.data))
      .catch((err) => {
        console.error("Failed to get API response" + err);
      });

  }, []);

  /* POST: Adding New Tools with modal */
  const addTool = () => {
    api.post("", {
      title: toolName,
      link: toolLink,
      description: toolDescription,
      tags: toolTags
    }
    )
  }

  /* DELETE: Deleting by ID Tools with confirmation modal */
  const removeTool = (id) => {
    api.delete(`${id}`);
  }

  /* Search Tools in API */
  const searchTool = (input) => {
    if (!checkbox) {

      /* GET: Searching by all positions in API */
      api
        .get(`?q=${input}`)
        .then((response) => setTools(response.data))
        .catch((err) => {
          console.error("Failed to get API response" + err);
        });

    } else {

      /* GET: Searching by TAGS position in API */
      api
        .get(`?tags_like=${input}`)
        .then((response) => setTools(response.data))
        .catch((err) => {
          console.error("Failed to get API response" + err);
        });
    }
  }

  /* Starting Render HTML components */
  return (


    <div className="App">
      <Container className="pt-5">
        <Row className="pt-2">
          <h1>VUTTR</h1>
        </Row>

        <Row className="pt-2">
          <h2>Very Useful Tools to Remeber</h2>
        </Row>

        <Row className="pt-2">
          <Col>
            <input 
                type="text" 
                placeholder="Search" 
                value={inputSearch}
                onChange={(e) => setInputSearch(e.target.value)}>
            </input>

            <spam className="p-2">
              <button onClick={() => searchTool(inputSearch)}>Search</button>
            </spam>

            <input 
              type="checkbox"
              onChange={() => {
                if (checkbox) {
                  setCheckbox(false);
                } else {
                  setCheckbox(true);
                }
              }
            }>
            </input>

            <spam className="p-2">Search in tags only</spam>
          </Col>

          <Col>
            <button className="float-end" onClick={() => handleShowModalAdd()}>Add</button>
          </Col>
        </Row>

            <Row className="pt-5">
          <div className="list">


        {tools.map(tools => (

          <span key={tools.title} className="pt-5">
            <Col><h2>{tools.title} &nbsp; </h2>
            </Col>
            <Col>
            <h2 className="float-end pe-auto" onClick={() => {
                setSelectedTool(tools.id);
                handleShowModalRemove()
              }}>x remove</h2>
            </Col>

            <br />

              {tools.description}<br />

              {tools.tags.map(tags => (
                <strong><span key={tags}>#{tags}  &nbsp; </span></strong>
              ))}



          </span>
        ))}

      </div>
      </Row>


      {/* MODAL QUE PARA ADICIONAR FERRAMENTAS */}

      <Modal show={showModalAdd} onHide={() => handleCloseModalAdd()}>
        <Modal.Header closeButton>
          <Modal.Title>+ Add new tool</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h1>Tool Name</h1>
          <input
            type="text"
            value={toolName}
            onChange={(e) => setToolName(e.target.value)}
          />
          <br />

          <h1>Tool Link</h1>
          <input
            type="text"
            value={toolLink}
            onChange={(e) => setToolLink(e.target.value)}
          />
          <br />

          <h1>Tool description</h1>
          <input
            type="text"
            value={toolDescription}
            onChange={(e) => setToolDescription(e.target.value)}
          />
          <br />

          <h1>Tags</h1>
          <input
            type="text"
            value={toolTags}
            onChange={(e) => setToolTags(e.target.value)}
          />

        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => { addTool(); handleCloseModalAdd() }}>
            Add tool
          </Button>
        </Modal.Footer>
      </Modal>

      {/* MODAL QUE PARA REMOVER FERRAMENTAS */}

      <Modal show={showModalRemove} onHide={() => handleCloseModalRemove()}>
        <Modal.Header closeButton>
          <Modal.Title>x Remove tool</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h1>Are you sure you want to remove  ?</h1>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => { removeTool(selectedTool); handleCloseModalRemove() }}>
            Yes, remove
          </Button>
          <Button variant="secondary" onClick={() => { handleCloseModalRemove() }}>
            Cancel
          </Button>
        </Modal.Footer>
      </Modal>
      </Container>
    </div >
  );
}
